import { useEffect, useState } from "react";
import { getExams } from "../services/examService";

function ExamList() {
  const [exams, setExams] = useState([]);

  useEffect(() => {
    const fetchExams = async () => {
      const data = await getExams();
      const sorted = data.sort((a, b) => new Date(a.date) - new Date(b.date));
      setExams(sorted);
    };
    fetchExams();
  }, []);

  return (
    <div>
      <h2 className="text-3xl font-bold mb-6 text-center text-blue-700">ðŸ“… Upcoming Exams</h2>

      {exams.length === 0 ? (
        <p className="text-center text-gray-500">No exams added yet.</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {exams.map((exam) => (
            <div
              key={exam._id}
              className="border border-gray-300 rounded-lg shadow-md p-6 hover:shadow-xl transition-shadow duration-300 bg-white"
            >
              <h3 className="text-xl font-semibold text-gray-800 mb-2">{exam.title}</h3>
              <p className="text-gray-600 mb-3">
                <span className="font-semibold">Date:</span>{" "}
                {new Date(exam.date).toLocaleDateString()}
              </p>
              <a
                href={exam.officialLink}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition-colors"
              >
                View Official Notification
              </a>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default ExamList;
